<?php
define("CAPTCHA_LENGTH", 6);
define("CAPTCHA_ALLOWED_CHARS", 'ABCDEFGHKLMNPQRTUVWXY346789');
session_name("CAPTCHA_SESSION");
session_start();
ob_start();

if (! isset($_SESSION['orig_uri'])) {
    $_SESSION['orig_uri'] = $_SERVER['HTTP_X_FORWARDED_URI'] ?? "";
}

if (! isset($_SESSION['auth'])) {
    $_SESSION['auth'] = "NO";
}

if ($_SESSION['auth'] === "YES") {
    exit();
}

header("HTTP/1.0 403 Forbidden");
$u = $_SERVER['HTTP_X_FORWARDED_URI'] ?? $_SERVER['REQUEST_URI'];
$v = substr($u, strpos($u, "?") + 1);
parse_str($v, $vars);

if (strtoupper($vars['captcha'] ?? '') === ($_SESSION['captcha'] ?? null)) {
    $_SESSION['auth'] = "YES";
    ?>
<html>
<head>
    <script>
        window.location.href = "<?php
    echo $_SESSION['orig_uri'];
    ?>";
    </script>
</head>
</html>
<?php
    exit();
}

$captcha = '';

while (strlen($captcha) < CAPTCHA_LENGTH) {
    $captcha .= CAPTCHA_ALLOWED_CHARS[random_int(0, strlen(CAPTCHA_ALLOWED_CHARS) - 1)];
}

$_SESSION['captcha'] = $captcha;
$image = imagecreate(60, 20);

$bg = imagecolorallocate(
    $image,
    random_int(150, 255),
    random_int(150, 255),
    random_int(150, 255),
);

$textcolor = imagecolorallocate(
    $image,
    random_int(0, 100),
    random_int(0, 100),
    random_int(0, 100),
);

imagestring($image, random_int(3, 5), 2, 2, $captcha, $textcolor);
$image = imagerotate($image, random_int(-10, 10), $bg);
ob_start();
imagepng($image);
$imagedata = ob_get_clean();
$datauri = "data:image/png;base64," . base64_encode($imagedata);
?>
<style>
    body{
        font-family: Arial, sans-serif;
        background-color: #e8e8e8;
        color: #333;
    }
    img{
        margin: 10px;
    }
    form{
        margin: 10px;
    }
    #captcha{
        border: 1px solid #333;
        border-radius: 5px;
    }
</style>
<form method="get">
    <img style="height:200px;width: 200px; float:right" src="data:image/png;base64," />
    <img id="captcha" style="height:40px;width: 120px;" src="<?php
echo $datauri;
?>" /><br>
    Please enter the text from the image above:<br>

    <input type="text" name="captcha" placeholder="Captachatext" title="Captcha" required tabindex="1" autofocus>
    <input type="submit" value="Ok">
</form>
